sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	// Declare empty array for answer
	var answer = [];

	// Get the VIN from the path parameter. Generate 404 if not provided.
	var vin = request.pathParams.vin;
	if (!vin) {
		response.setError(new sn_ws_err.NotFoundError('VIN not provided.'));
		response.setStatus(404);
		return;
	}

	// Query the Vehicle table for this VIN.
	var vehicleTable = 'x_1281635_vehicles_vehicle';
	var vehicleGr = new GlideRecord(vehicleTable);
	vehicleGr.addQuery('number', vin);
	vehicleGr.query();

	// Generate 404 if no vehicle was found.
	if (!vehicleGr.hasNext()) {
		response.setError(new sn_ws_err.NotFoundError('Record not found.'));
		response.setStatus(404);
		return;
	}

	// Generate JSON object to store Vehicle if found
	if (vehicleGr.next()) {
		var vehicleObj = {
			"name": vehicleGr.getValue('name'),
			"description": vehicleGr.getValue('description'),
			"model": vehicleGr.getValue('model'),
			"model_year": vehicleGr.getValue('model_year')
		};

		answer.push(vehicleObj);
	}

	// Generate the body.
	response.setBody(answer);
})(request, response);
