sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    // Define empty answer
    var answer = [];

    // Get values from query parameters
    var name = request.queryParams.name;
    var model = request.queryParams.model;
    var description = request.queryParams.description;
    var model_year = request.queryParams.model_year;

    // Generate 404 if no query parameters were specified
    if (!name && !model && !description && !model_year) {
        response.setError(
            new sn_ws_err.NotFoundError("No query parameters were specified.")
        );
        response.setStatus(404);
        return;
    }

    // Query the Vehicle table using these parameters
    var vehicleTable = "x_1281635_vehicles_vehicle";
    var vehicleGr = new GlideRecordSecure(vehicleTable);
    if (name) {
        vehicleGr.addQuery("name", name);
    }
    if (model) {
        vehicleGr.addQuery("model", model);
    }
    if (description) {
        vehicleGr.addQuery("description", description);
    }
    if (model_year) {
        vehicleGr.addQuery("model_year", model_year);
    }
    vehicleGr.query();

    // Generate 404 error responses if no Vehicle found
    if (!vehicleGr.hasNext()) {
        response.setError(
            new sn_ws_err.NotFoundError(
                "No Vehicle record found with the specified criteria."
            )
        );
        response.setStatus(404);
        return;
    }

    // Generate JSON object
    while (vehicleGr.next()) {
        var vehicleObj = {
            vin: vehicleGr.getValue("number"),
            name: vehicleGr.getValue("name"),
            model: vehicleGr.getValue("model"),
            description: vehicleGr.getValue("description"),
            model_year: vehicleGr.getValue("model_year"),
        };
        answer.push(vehicleObj);
    }

    // Generate response body
    response.setBody(answer);
})(request, response);
